mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.create_time, d.update_time, d.create_user, d.update_user, c.name as categoryName from wuwei_sky_take_out.dish d left join wuwei_sky_take_out.category c on d.category_id = c.id
and d.name like concat('%',#{name}, '%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
order by create_time desc

mapper.update:
UPDATE wuwei_sky_take_out.dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.delete:
delete from wuwei_sky_take_out.dish
where id in

    #{id}
